<script>
export default {
  data: () => ({
    active_menu: "Categories"
  }),
  created() {
    this.active_menu = this.$store.state.provider_menu;
  },
  methods: {
    async logout() {
      this.$store.commit("changeProviderMenu", "Categories");
      await this.$auth.logout().then(() => {
        this.$router.push("/");
      });
    },
    changeMenu(value) {
      this.active_menu = value;
      this.$store.commit("changeProviderMenu", value);
      this.$router.push(`/provider/${value}`);
    }
  }
};
</script>

